import React from 'react';
import { motion } from 'framer-motion';

const Education = () => {
  const fadeIn = {
    initial: { opacity: 0, y: 20 },
    animate: { opacity: 1, y: 0 },
    transition: { duration: 0.6, ease: [0.22, 1, 0.36, 1] }
  };

  const educationData = [
    {
      degree: "Master of Science in Data Science",
      university: "University of New Hampshire",
      date: "2023 - Present",
      gpa: "3.95/4.0",
      keyLearnings: [
        "Advanced Machine Learning",
        "Deep Learning & Neural Networks",
        "Big Data Analytics",
        "Statistical Analysis"
      ]
    },
    {
      degree: "Bachelor of Science in Computer Science",
      university: "Example University",
      date: "2019 - 2023",
      gpa: "3.85/4.0",
      keyLearnings: [
        "Data Structures & Algorithms",
        "Software Engineering",
        "Database Systems",
        "Computer Networks"
      ]
    }
  ];

  return (
    <section id="education" className="section">
      <motion.div
        initial="initial"
        whileInView="animate"
        viewport={{ once: true }}
        variants={{
          animate: { transition: { staggerChildren: 0.1 } }
        }}
      >
        <motion.h2 
          variants={fadeIn}
          className="text-h2 text-text mb-8"
        >
          Education
        </motion.h2>

        <div className="space-y-8">
          {educationData.map((edu, index) => (
            <motion.div
              key={index}
              variants={fadeIn}
              className="bg-background-dark rounded-xl p-6 md:p-8"
            >
              <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-4">
                <div>
                  <h3 className="text-h3 text-text">{edu.degree}</h3>
                  <p className="text-body text-text-light">{edu.university}</p>
                </div>
                <div className="mt-2 md:mt-0 text-right">
                  <p className="text-body text-primary">{edu.date}</p>
                  <p className="text-body text-text-light">GPA: {edu.gpa}</p>
                </div>
              </div>
              
              <div>
                <h4 className="text-body font-semibold mb-2">Key Learnings:</h4>
                <ul className="grid grid-cols-1 md:grid-cols-2 gap-2">
                  {edu.keyLearnings.map((learning, idx) => (
                    <li key={idx} className="text-body text-text-light flex items-center">
                      <span className="w-2 h-2 bg-primary rounded-full mr-2" />
                      {learning}
                    </li>
                  ))}
                </ul>
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>
    </section>
  );
};

export default Education;
