      issuer: "Coursera",
      issueDate: "Aug 2022",
      credentialId: "GFWM5QA43RY",
      image: "/images/cert-regex.svg",
      verifyLink: "#"
    },
    {
      title: "Exploratory Data Analysis with Seaborn",
      issuer: "Coursera",
      issueDate: "Aug 2022",
      credentialId: "PPMLAYEV47GT",
      image: "/images/cert-seaborn.svg",
      verifyLink: "#"
    },
    {
      title: "Python HackerRank Certificate",
      issuer: "HackerRank",
      issueDate: "Jul 2022",
      credentialId: "68F64C1F92A6",
      image: "/images/cert-python-hr.svg",
      verifyLink: "#"
    },
    {
