import React, { useState } from 'react';
import { motion } from 'framer-motion';

const ProjectCard = ({ title, description, tech, image, links }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const shortDescription = description.slice(0, 100) + (description.length > 100 ? '...' : '');

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.5 }}
      className="bg-[#282C33] border border-border rounded-lg overflow-hidden"
    >
      {/* Project Image */}
      <div 
        className="h-48 bg-cover bg-center relative group"
        style={{ backgroundImage: `url(${image})` }}
      >
        <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
          <div className="flex gap-4">
            {links.map((link, index) => (
              <a
                key={index}
                href={link.url}
                className="px-4 py-2 bg-background border border-accent text-accent hover:bg-accent hover:text-white transition-all rounded"
              >
                {link.text}
              </a>
            ))}
          </div>
        </div>
      </div>

      {/* Project Info */}
      <div className="p-6 space-y-4">
        {/* Tech Stack */}
        <div className="text-text-secondary text-sm font-mono">
          {tech}
        </div>

        {/* Title */}
        <h3 className="text-xl font-mono">{title}</h3>

        {/* Description */}
        <div className="text-text-secondary">
          <p>{isExpanded ? description : shortDescription}</p>
          {description.length > 100 && (
            <button
              onClick={() => setIsExpanded(!isExpanded)}
              className="text-accent hover:text-accent/80 transition-colors mt-2 text-sm"
            >
              {isExpanded ? 'Show less' : 'Read more'}
            </button>
          )}
        </div>

        {/* Action Buttons */}
        <div className="flex gap-4 pt-2">
          {links.map((link, index) => (
            <a
              key={index}
              href={link.url}
              className="px-4 py-1 border border-text-secondary text-text-secondary hover:border-accent hover:text-accent transition-colors rounded text-sm"
            >
              {link.text} {link.icon}
            </a>
          ))}
        </div>
      </div>
    </motion.div>
  );
};

const Projects = () => {
  const completeApps = [
    {
      title: "ChertNodes",
      description: "Minecraft servers hosting platform with an intuitive control panel for easy server management. Features include automatic backups, custom domain support, and mod installation.",
      tech: "HTML SCSS Python Flask",
      image: "/images/chertnodes.svg",
      links: [
        { text: "Live", url: "#", icon: "↗" },
        { text: "Cached", url: "#", icon: ">" }
      ]
    },
    {
      title: "ProtectX",
      description: "Discord anti-crash bot with advanced security features. Protects servers from raids, spam, and malicious content while providing detailed logging and customizable settings.",
      tech: "React Express Discord.js Node.js",
      image: "/images/protectx.svg",
      links: [
        { text: "Live", url: "#", icon: "↗" }
      ]
    },
    {
      title: "Kahoot Answers Viewer",
      description: "Tool for viewing Kahoot quiz answers in real-time. Uses advanced algorithms to analyze and display correct answers during live games.",
      tech: "CSS Express Node.js",
      image: "/images/kahoot.svg",
      links: [
        { text: "Live", url: "#", icon: "↗" }
      ]
    }
  ];

  const smallProjects = [
    {
      title: "Bot boilerplate",
      description: "Start creating scalable discord.js bot with typescript in seconds",
      tech: "Discord.js TS JS",
      links: [
        { text: "Github", url: "#", icon: "↗" }
      ]
    },
    {
      title: "My blog",
      description: "Front-end of my future blog website written in vue",
      tech: "Vue CSS JS",
      links: [
        { text: "Github", url: "#", icon: "↗" }
      ]
    }
  ];

  return (
    <section id="projects" className="py-20 px-8 lg:px-24">
      <div className="max-w-7xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          className="mb-12"
        >
          <div className="flex items-center justify-between mb-8">
            <h2 className="text-2xl font-mono">
              <span className="text-accent">#</span>projects
            </h2>
            <a href="#" className="text-text-secondary hover:text-accent transition-colors">
              View all ~~&gt;
            </a>
          </div>
          <p className="text-text-secondary">List of my projects</p>
        </motion.div>

        {/* Complete Apps */}
        <div className="mb-16">
          <motion.h3
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            className="text-xl mb-6"
          >
            <span className="text-accent">#</span>complete-apps
          </motion.h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {completeApps.map((project, index) => (
              <ProjectCard key={index} {...project} />
            ))}
          </div>
        </div>

        {/* Small Projects */}
        <div>
          <motion.h3
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            className="text-xl mb-6"
          >
            <span className="text-accent">#</span>small-projects
          </motion.h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {smallProjects.map((project, index) => (
              <ProjectCard key={index} {...project} />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Projects;
