import React from 'react';
import PropTypes from 'prop-types';

const BlogCard = ({ title, date, description, tags, image }) => (
  <div className="border border-[#ABB2BF] group hover:border-[#C778DD] transition-colors">
    <div className="aspect-video bg-[#282C33] overflow-hidden">
      <img 
        src={image} 
        alt={title}
        className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
      />
    </div>
    <div className="p-4">
      <div className="flex gap-2 mb-2">
        {tags.map((tag, index) => (
          <span key={index} className="text-sm text-[#C778DD]">#{tag}</span>
        ))}
      </div>
      <h3 className="text-xl font-bold mb-2">{title}</h3>
      <p className="text-[#ABB2BF] mb-4">{description}</p>
      <div className="flex justify-between items-center">
        <span className="text-sm text-[#ABB2BF]">{date}</span>
        <button className="border border-[#C778DD] px-4 py-2 hover:bg-[#C778DD] hover:text-white transition-colors">
          Read more â†’
        </button>
      </div>
    </div>
  </div>
);

BlogCard.propTypes = {
  title: PropTypes.string.isRequired,
  date: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  tags: PropTypes.arrayOf(PropTypes.string).isRequired,
  image: PropTypes.string.isRequired,
};

const Blog = () => {
  const posts = [
    {
      title: "Creating Custom React Hooks",
      date: "2024-03-15",
      description: "Learn how to create and use custom React hooks to improve code reusability and maintainability.",
      tags: ["react", "javascript", "webdev"],
      image: "/images/blog-1.jpg"
    },
    {
      title: "Modern CSS Techniques",
      date: "2024-03-10",
      description: "Explore modern CSS features and techniques for building beautiful user interfaces.",
      tags: ["css", "webdesign", "frontend"],
      image: "/images/blog-2.jpg"
    },
    {
      title: "Getting Started with TypeScript",
      date: "2024-03-05",
      description: "A comprehensive guide to start using TypeScript in your JavaScript projects.",
      tags: ["typescript", "javascript", "programming"],
      image: "/images/blog-3.jpg"
    }
  ];

  return (
    <section id="blog" className="py-20 px-4 md:px-8">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold mb-8">
          <span className="text-[#C778DD]">#</span>blog
        </h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {posts.map((post, index) => (
            <BlogCard key={index} {...post} />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Blog;
