import React, { useState } from 'react';
import PropTypes from 'prop-types';

const ProjectCard = ({ title, description, tech, image, links }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  const truncatedDescription = isExpanded ? description : description.slice(0, 100) + (description.length > 100 ? '...' : '');

  return (
    <div className="border border-[#ABB2BF] group hover:border-[#C778DD] transition-colors">
      <div className="aspect-video bg-[#282C33] overflow-hidden">
        <img 
          src={image} 
          alt={title}
          className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
        />
      </div>
      <div className="p-4">
        <div className="flex gap-2 mb-2">
          {tech.map((item, index) => (
            <span key={index} className="text-sm text-[#C778DD]">#{item}</span>
          ))}
        </div>
        <h3 className="text-xl font-bold mb-2">{title}</h3>
        <p className="text-[#ABB2BF] mb-4">
          {truncatedDescription}
          {description.length > 100 && (
            <button
              onClick={() => setIsExpanded(!isExpanded)}
              className="text-[#C778DD] hover:underline ml-2"
            >
              {isExpanded ? 'Show less' : 'Read more'}
            </button>
          )}
        </p>
        <div className="flex gap-4">
          {links.map((link, index) => (
            <a
              key={index}
              href={link.url}
              target="_blank"
              rel="noopener noreferrer"
              className="border border-[#C778DD] px-4 py-2 hover:bg-[#C778DD] hover:text-white transition-colors"
            >
              {link.text}
            </a>
          ))}
        </div>
      </div>
    </div>
  );
};

ProjectCard.propTypes = {
  title: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  tech: PropTypes.arrayOf(PropTypes.string).isRequired,
  image: PropTypes.string.isRequired,
  links: PropTypes.arrayOf(
    PropTypes.shape({
      text: PropTypes.string.isRequired,
      url: PropTypes.string.isRequired,
    })
  ).isRequired,
};

const Projects = () => {
  const projects = [
    {
      title: "ChertNodes",
      description: "Minecraft servers hosting using React, Express.js and Cloud Computing Services.",
      tech: ["react", "express", "cloud"],
      image: "/images/chertnodes.svg",
      links: [
        { text: "Live <~>", url: "#" },
        { text: "Code </>", url: "#" }
      ]
    },
    {
      title: "ProtectX",
      description: "Discord anti-crash bot with advanced security features using Discord.js.",
      tech: ["discord.js", "node.js"],
      image: "/images/protectx.svg",
      links: [
        { text: "Live <~>", url: "#" },
        { text: "Code </>", url: "#" }
      ]
    },
    {
      title: "Kahoot Answers Viewer",
      description: "Get answers to your Kahoot quiz using this Chrome extension.",
      tech: ["chrome", "javascript"],
      image: "/images/kahoot.svg",
      links: [
        { text: "Live <~>", url: "#" },
        { text: "Code </>", url: "#" }
      ]
    }
  ];

  return (
    <section id="projects" className="py-20 px-4 md:px-8">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold mb-8">
          <span className="text-[#C778DD]">#</span>projects
        </h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project, index) => (
            <ProjectCard key={index} {...project} />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
