import React from 'react';
import PropTypes from 'prop-types';

const CertificateCard = ({ title, issuer, date, image, link }) => (
  <div className="border border-[#ABB2BF] group hover:border-[#C778DD] transition-colors">
    <div className="aspect-video bg-[#282C33] overflow-hidden">
      <img 
        src={image} 
        alt={title}
        className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
      />
    </div>
    <div className="p-4">
      <h3 className="text-xl font-bold mb-2">{title}</h3>
      <p className="text-[#C778DD] mb-2">{issuer}</p>
      <p className="text-[#ABB2BF] text-sm mb-4">{date}</p>
      <a 
        href={link}
        target="_blank"
        rel="noopener noreferrer"
        className="inline-block border border-[#C778DD] px-4 py-2 hover:bg-[#C778DD] hover:text-white transition-colors"
      >
        View Certificate â†’
      </a>
    </div>
  </div>
);

CertificateCard.propTypes = {
  title: PropTypes.string.isRequired,
  issuer: PropTypes.string.isRequired,
  date: PropTypes.string.isRequired,
  image: PropTypes.string.isRequired,
  link: PropTypes.string.isRequired,
};

const Certificates = () => {
  const certificates = [
    {
      title: "Advanced Web Development",
      issuer: "Udacity",
      date: "March 2024",
      image: "/images/cert-1.jpg",
      link: "#"
    },
    {
      title: "React Development",
      issuer: "Meta",
      date: "February 2024",
      image: "/images/cert-2.jpg",
      link: "#"
    },
    {
      title: "Full Stack Development",
      issuer: "FreeCodeCamp",
      date: "January 2024",
      image: "/images/cert-3.jpg",
      link: "#"
    }
  ];

  return (
    <section id="certificates" className="py-20 px-4 md:px-8">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold mb-8">
          <span className="text-[#C778DD]">#</span>certificates
        </h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {certificates.map((cert, index) => (
            <CertificateCard key={index} {...cert} />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Certificates;
