import React from 'react';
import { motion } from 'framer-motion';

const SkillSet = () => {
  const fadeIn = {
    initial: { opacity: 0, y: 20 },
    animate: { opacity: 1, y: 0 },
    transition: { duration: 0.6, ease: [0.22, 1, 0.36, 1] }
  };

  const skillCategories = [
    {
      title: "Mathematics & Core Concepts",
      skills: [
        "Linear Algebra",
        "Calculus",
        "Statistics",
        "Probability Theory",
        "Optimization",
        "Algorithm Design"
      ]
    },
    {
      title: "Languages",
      skills: [
        "Python",
        "R",
        "SQL",
        "JavaScript",
        "Java",
        "C++"
      ]
    },
    {
      title: "Machine Learning & AI",
      skills: [
        "TensorFlow",
        "PyTorch",
        "Scikit-learn",
        "Deep Learning",
        "NLP",
        "Computer Vision"
      ]
    },
    {
      title: "Data Visualization & Analytics",
      skills: [
        "Tableau",
        "Power BI",
        "Matplotlib",
        "Seaborn",
        "D3.js",
        "Plotly"
      ]
    },
    {
      title: "Web & API Development",
      skills: [
        "React",
        "Node.js",
        "FastAPI",
        "Flask",
        "RESTful APIs",
        "GraphQL"
      ]
    },
    {
      title: "DevOps & Cloud",
      skills: [
        "AWS",
        "Docker",
        "Kubernetes",
        "CI/CD",
        "Git",
        "Linux"
      ]
    }
  ];

  return (
    <section id="skills" className="section">
      <motion.div
        initial="initial"
        whileInView="animate"
        viewport={{ once: true }}
        variants={{
          animate: { transition: { staggerChildren: 0.1 } }
        }}
      >
        <motion.h2 
          variants={fadeIn}
          className="text-h2 text-text mb-8"
        >
          Skill Set
        </motion.h2>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {skillCategories.map((category, index) => (
            <motion.div
              key={index}
              variants={fadeIn}
              className="bg-background-dark rounded-xl p-6"
            >
              <h3 className="text-h3 text-text mb-4">{category.title}</h3>
              <div className="flex flex-wrap gap-2">
                {category.skills.map((skill, idx) => (
                  <span
                    key={idx}
                    className="px-3 py-1 bg-background text-text-light rounded-full text-small"
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>
    </section>
  );
};

export default SkillSet;
