import React from 'react';
import { motion } from 'framer-motion';

const Home = () => {
  return (
    <section id="home" className="min-h-screen px-8 lg:px-24 pt-32">
      <div className="max-w-7xl mx-auto">
        {/* Main Content */}
        <div className="flex flex-col lg:flex-row items-start justify-between gap-12">
          {/* Left Content */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="lg:w-1/2 space-y-6"
          >
            <h1 className="text-4xl lg:text-6xl font-mono">
              Elias is a{' '}
              <span className="text-accent">web designer</span> and{' '}
              <br className="hidden lg:block" />
              <span className="text-accent">front-end developer</span>
            </h1>

            <p className="text-text-secondary text-lg">
              He crafts responsive websites where technologies meet creativity
            </p>

            <button className="px-8 py-2 border border-accent text-accent hover:bg-accent hover:text-white transition-colors">
              Contact me!!
            </button>
          </motion.div>

          {/* Right Content with Image */}
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6 }}
            className="lg:w-1/2 relative"
          >
            {/* Decorative Squares */}
            <div className="absolute -right-4 top-4">
              <div className="relative w-16 h-16">
                <div className="absolute border border-accent w-full h-full"></div>
                <div className="absolute border border-accent w-full h-full rotate-45"></div>
              </div>
            </div>

            {/* Image Container */}
            <div className="relative">
              <img
                src="/images/profile.svg"
                alt="Elias"
                className="w-full rounded-lg"
              />

              {/* Dots Pattern */}
              <div className="absolute -right-8 bottom-8 grid grid-cols-4 gap-2">
                {[...Array(16)].map((_, i) => (
                  <div key={i} className="w-2 h-2 bg-accent rounded-full"></div>
                ))}
              </div>

              {/* Currently Working Badge */}
              <div className="absolute -bottom-6 left-6 right-6 bg-background p-2 border border-border rounded">
                <div className="flex items-center gap-2">
                  <div className="w-2 h-2 bg-accent rounded-full"></div>
                  <span className="text-accent">Currently working on</span>
                  <span className="text-text-secondary">Portfolio</span>
                </div>
              </div>
            </div>
          </motion.div>
        </div>

        {/* Quote Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
          className="mt-32 relative max-w-2xl"
        >
          {/* Quote Box */}
          <div className="relative">
            {/* Decorative Elements */}
            <div className="absolute -left-12 -top-8 w-24 h-24">
              <div className="relative w-full h-full">
                <div className="absolute border border-accent/20 w-full h-full"></div>
                <div className="absolute border border-accent/20 w-full h-full rotate-45"></div>
              </div>
            </div>
            <div className="absolute -right-8 -bottom-8 grid grid-cols-4 gap-2">
              {[...Array(16)].map((_, i) => (
                <div key={i} className="w-2 h-2 bg-accent/20 rounded-full"></div>
              ))}
            </div>

            {/* Quote Content */}
            <div className="border border-accent/20 rounded p-8 bg-background">
              <div className="relative">
                <span className="absolute -top-6 -left-4 text-4xl text-accent">&ldquo;</span>
                <p className="text-xl font-mono">
                  With great power comes great electricity bill
                </p>
                <div className="flex justify-end mt-4">
                  <p className="text-text-secondary">- Dr. Who</p>
                </div>
                <span className="absolute -bottom-6 -right-4 text-4xl text-accent rotate-180">&rdquo;</span>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Home;
