import React, { useState } from 'react';
import Navigation from './components/Navigation';
import Home from './components/Home';
import About from './components/About';
import Experience from './components/Experience';
import Projects from './components/Projects';
import Blog from './components/Blog';
import Certificates from './components/Certificates';
import Leadership from './components/Leadership';
import Contact from './components/Contact';
import Footer from './components/Footer';

function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <div className="bg-[#282C33] text-white min-h-screen">
      <header className="fixed top-0 left-0 right-0 bg-[#282C33] z-40 border-b border-[#ABB2BF]">
        <div className="max-w-6xl mx-auto px-4 md:px-8 py-6">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-2">
              <div className="w-8 h-8 border border-[#C778DD] grid grid-cols-2">
                <div className="bg-[#C778DD]"></div>
              </div>
              <span className="text-2xl font-mono">Elias</span>
            </div>

            {/* Desktop Navigation */}
            <nav className="hidden md:flex items-center space-x-8">
              <a href="#home" className="nav-link"><span className="hash-tag">#</span>home</a>
              <a href="#about" className="nav-link"><span className="hash-tag">#</span>about</a>
              <a href="#experience" className="nav-link"><span className="hash-tag">#</span>experience</a>
              <a href="#projects" className="nav-link"><span className="hash-tag">#</span>projects</a>
              <a href="#blog" className="nav-link"><span className="hash-tag">#</span>blog</a>
              <a href="#certificates" className="nav-link"><span className="hash-tag">#</span>certificates</a>
              <a href="#leadership" className="nav-link"><span className="hash-tag">#</span>leadership</a>
              <a href="#contact" className="nav-link"><span className="hash-tag">#</span>contact</a>
              <button className="nav-link">EN</button>
            </nav>

            {/* Mobile Menu Button */}
            <button 
              className="md:hidden p-2"
              onClick={() => setIsMenuOpen(true)}
            >
              <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>
      </header>

      {/* Mobile Navigation */}
      {isMenuOpen && (
        <Navigation onClose={() => setIsMenuOpen(false)} />
      )}

      <main className="pt-24">
        <Home />
        <About />
        <Experience />
        <Projects />
        <Blog />
        <Certificates />
        <Leadership />
        <Contact />
      </main>

      <Footer />
    </div>
  );
}

export default App;
